import { MapContainer, Marker, TileLayer, Tooltip, Popup } from "react-leaflet"
import { Polyline } from "react-leaflet"
import "leaflet/dist/leaflet.css"
import { useEffect, useState } from "react"
import axios from "axios"

export default function MyMap(props){
  // console.log(props.lat)
  // const position = [-7.290091,112.797018]
  var myIcon = L.icon({
    iconUrl: 'data:image/png;base64,',
    iconSize: [20, 32]
  })
  
  var pinIcon = L.icon({
    iconUrl: 'https://img.icons8.com/?size=100&id=30568&format=png&color=000000',
    iconSize: [20, 32]
  })

  let lat = props.lat
  let lon = props.lon
  const [pin, setPin] = useState([])

  // if(latitude !== null) {
  //   lat = latitude
  //   lon = longitude
  // }

  var latlngs = []
  for (let i = 0; i < lat.length; i++) {
    latlngs[i] = [lat[i],lon[i]]
  }

  var dest = [lat[lat.length-1], lon[lon.length-1]]

  useEffect(()=>{
    async function getresto() {
      const resp = await axios.post('../../api/getlatlon')
      if(resp) setPin(resp.data)
        // console.log(pin)
    }getresto()
  })
  var pinpoint = []
  // var pinpoint = [
  //   [-7.287497,112.79469],
  //   [-7.283804,112.794089],
  //   [-7.280084,112.797003],
  //   [-7.287635,112.793887],
  //   [-7.288242,112.795781]
  // ]
  pin.map((pin,index)=>(
    pinpoint[index] = [pin.lat, pin.lon]
  ))
  // console.log(pinpoint)
  let marker = []
 for (let i = 0; i < pinpoint.length; i++) {
  if (JSON.stringify(props.dest)!==JSON.stringify(pinpoint[i])){
    marker.push(
      <Marker key={i} position={pinpoint[i]} icon={pinIcon}>
        <Popup>
          A pretty CSS3 popup. <br /> Easily customizable.
        </Popup>
      </Marker>
    )
  }
 }
  

  // var polyline = L.polyline(latlngs, {color: 'red'})

  return (
    <MapContainer className=" w-full h-80" center={dest} zoom={15} scrollWheelZoom={false}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <Marker position={dest} icon={myIcon}>
        <Popup>
          A pretty CSS3 popup. <br /> Easily customizable.
        </Popup>
      </Marker>
      {marker}
      <Polyline positions={latlngs} color={'red'}></Polyline>
    </MapContainer>
  );
}